create table Incident (IncidentTicketNumber varchar(10) primary key, IncidentType ENUM ('Cloud','Virtualization', 'Network', 'System', 'Collabration', 'Computer', 'Printer', 'Scanner', 'IP Phone', 'Utility'), IncidentDetectionDate datetime, IncidentDuration varchar (20), IncidentAgeDay int, IncidentAgeHour int, IncidentAgeMinute int, IncidentHierarchyTier1 ENUM('Request', 'Report', 'Feedback'), IncidentHierarchyTier2 ENUM('Hardware', 'Software', 'Change', 'Procedure', 'Incident'), IncidentHierarchyTier3 varchar (50), IncidentIdentificationDate date, IncidentReportedHour time, IncidentStatus varchar (20), IncidentSummary varchar(50));
create table Service (ServiceCategory ENUM ('Print Service', 'Email Service', 'Cloud Service', 'Internet Service'), ServiceStartDate datetime, ServiceStopDate datetime);
