create table SubjectPredicateObjectTable (Subject varchar(20), Predicate varchar (20), Object varchar(20), ID varchar(10) primary key );

create table Location (Location_ID varchar (10) primary key , Country varchar (40), Continent varchar (40), Region varchar (40), City varchar (40), Building varchar (40), Room_Number varchar (40));
create table Unit (Unit_Code varchar (10), Unit_Name varchar (30), Unit_Manager varchar (30), Unit_Members varchar (150), Number_Of_Employees int);
create table IT (IT_Sub_Unit_Name varchar (30), Contact_Person varchar(30), Email varchar(20) primary key);
create table Non_IT (Contact_Person varchar (20));
create table problem (Problem_Code INT AUTO_INCREMENT PRIMARY KEY, Problem_Start_Date date, Problem_End_Date date, Problem_Summary varchar (50), Problem_Category enum ('Power Issue', 'Internet Problem'));
create table Impact (Impact_Code INT auto_increment PRIMARY KEY, Impact_Summary varchar (100), Severe_Status enum ('High', 'Medium', 'Low'));
create table Pain_Value_Analysis (Number_of_Related_Incidents int, Last_Reported_Date datetime, Pain_Value double, Weighting_Factor varchar (20));
create table History (Incident_Reported_Date date, Incident_Reported_Hour time, Ticket_Age datetime, Aging_Status enum ('Normal', 'Aging', 'Aged'));
create table Communication (Sender varchar (30), Intended_Receiver varchar (30), message varchar (200));
create table Cost (Direct_Product_Cost double, Direct_Labour_Cost double, Direct_Expense_Cost double);
create table Threshold (Minimum_Number_Of_Related_Incidents int, Maximum_Number_Of_Related_Incidents int);
create table Resolution (Property_Number varchar (10), Asset_Category enum ('IP Phone', 'Computer', 'Printer', 'Scanner', 'Utility'), Asset_Name varchar (20), Subpart varchar (20), Root_Cause_Summary varchar (100), Operational_Category_Tier1 enum ('Cloud','Virtualization', 'Network', 'System', 'Collabration', 'Device'), Operational_Category_Tier2 enum ('Printer','Scanner', 'IP Phone', 'Utility', 'Computer', 'Microsoft Office 365'),  Operational_Category_Tier3 enum ('HP Laserjet 9050 Printer','Scanejet 2100', 'Cisco IP Phone', 'Outlook 2016', 'Lenovo T460s Laptop'), Resolution varchar (100), Resolution_Date date, Resolution_Time time);
create table Escalation (Escalated_From_Unit varchar (50), Escalated_To_Unit varchar (50), Escalated_From_Technician varchar (30), Escalation_Date date, Escalation_Time time, Attempted_Resolution_Summary varchar (100));
create table Closure (Closure_Date date, Closure_Time time, Workaround_Type enum ('Existed', 'New'), Workaround_Note varchar(1000));
create table Remediation (Temporary_Fix_Note varchar(100), Remediation_Date date, Remediation_Time time);
create table Workload (Number_Of_Assigned_Tickets int, Number_Of_Working_Tickets int, Number_Of_Closed_Tickets int, Number_Of_Opened_Tickets int, Number_Of_Escalated_Tickets int);
create table Performance (Resolution_Time datetime, Remediation_Time datetime, Escalation_Time datetime, Incident_Age datetime);
create table Client_Side (Fullname varchar (50), Department varchar (30), Country varchar (20), City varchar (15), Region varchar (20), Branch varchar (20), Room_Number varchar (5), Client_ID varchar (10) primary key);
create table Request (Request_Date date, Request_Time time, Request_Type varchar (30), Request_Category enum ('1st Time', '2nd Time', '3rd Time'), Request_Content varchar (500), Request_Summary varchar (100), Request_Hierarchy_Tier1 enum ('Hardware', 'Software', 'Procedure'), Request_Hierarchy_Tier2 enum ('Change', 'Replace', 'Borrow'),Request_Hierarchy_Tier3 enum('Install','Mouse', 'Laptop Charger'));
create table Signup (Signup_Date date, Signup_Time time, Signup_Service_Name varchar (30), Signup_Service_Status varchar(30));
create table Login (Username varchar (30), Client_Password varchar (30), Account_Type enum ('AD', 'Desktop', 'Email', 'Server', 'Internet','System'), User_Category enum ('Internal', 'External'));
